import React, { useState } from 'react';
import { ethers } from 'ethers';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
// Import your contract's ABI
import contractABI from './lottoAbi.json';



export default function DepositEther() {
  const [amount, setAmount] = useState('');

  // Create a provider
  let provider;
  if (window.ethereum) {
    provider = new ethers.providers.Web3Provider(window.ethereum);
  } else {
    // Handle the case where the user doesn't have Metamask installed
    console.error('Please install Metamask');
    return;
  }

  // Create a contract instance
  const contract = new ethers.Contract(contractAddress, contractABI, provider);

  const deposit = async () => {
    // Request account access if needed
    await window.ethereum.enable();

    const weiAmount = ethers.utils.parseEther(amount);

    // Get the user's signer
    const signer = provider.getSigner();

    // Connect the signer with the contract
    const contractWithSigner = contract.connect(signer);

    // Call depositEther with the amount to deposit
    const tx = await contractWithSigner.depositEther({ value: weiAmount });

    // Wait for the transaction to be mined
    await tx.wait();

    alert('Deposit successful!');
  };

  return (
    <div>
      <TextField 
        label="Amount" 
        value={amount} 
        onChange={e => setAmount(e.target.value)}
      />
      <Button variant="contained" color="primary" onClick={deposit}>
        Deposit Ether
      </Button>
    </div>
  );
}
